const siteTitle=document.getElementById("siteTitle");const jsonTxt=document.getElementById("jsonTxt");const ezcastList=document.getElementById("ezcastList");const id=getUrlParameter("id");if(id!=""){getSharedConfigById(id)}function onSharedConfigLoaded(a){console.log(a);siteTitle.innerHTML=a.configName;jsonTxt.value=JSON.stringify(a,null,2)}function saveAndApplySharedConfig(){if(id==""){addSharedConfig(jsonTxt.value)}else{alert("Editing is not yet supported!")}}function onSharedConfigAdded(){location.href="/pages/configs.php"}function getUrlParameter(a){a=a.replace(/[\[]/,"\\[").replace(/[\]]/,"\\]");var c=new RegExp("[\\?&]"+a+"=([^&#]*)");var b=c.exec(location.search);return b===null?"":decodeURIComponent(b[1].replace(/\+/g," "))};